mapper.select:
select

from t_user
where 1 = 1
and is_delete = 1

  and phone = #{phone}


  and oem_id = #{oemId}

select
from t_user where 1 = 1 and states = 1 and is_delete = 1
and phone = #{referPhone}
and oem_id = #{oemId}

mapper.update:
update t_user
set name = #{name,jdbcType=VARCHAR}
,identity = #{identity,jdbcType=VARCHAR}
,update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{userId,jdbcType=BIGINT}

mapper.select:
select

  from t_user
  where id = #{userId}
  and password = #{oldPassword}
  and states = 1
  and is_delete = 1

mapper.update:
update t_user
  set password = #{newPassword}
  ,update_time = #{updateTime,jdbcType=TIMESTAMP}
  where id = #{userId}
  and states = 1
  and is_delete = 1

update t_user
set isautopay = '1'
where id = #{userId}

update t_user
  set password = #{newPassword}
  ,update_time = #{updateTime,jdbcType=TIMESTAMP}
  where phone = #{mobilePhone}
